import { getTokenFromCookie, decodeToken } from '../../JWTProvider';
import './MainWindow.css'
import { useState } from 'react';
import { data, useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom'; // Импорт Link


export function MainWindow() {
    const [tokenData, setTokenData] = useState(getTokenFromCookie());
    const [isOpen, setIsOpen] = useState(false);


    const openModal = () => setIsOpen(true);
    const closeModal = () => setIsOpen(false);

    const notes = [
        { id: 1, title: 'Note 1', description: 'ljsljfl;s', createdAt: Date.now() },
        { id: 2, title: 'Note 2', description: 'ljsljfl;s 2', createdAt: Date.now() },
        { id: 3, title: 'Note 3', description: 'ljsljfl;s 3', createdAt: Date.now() },
    ];

    // const location = useLocation();
    // const sharedKey = location.state || ''

    // function handleGetTokenData() {
    //     return decodeToken(getTokenFromCookie()).nickName;
    // }

    return (
        <div className="container-main">
            <div className='center-container'>
                <div className='main-block'>

                    <div className='title-container'>
                        {/* <h3>{handleGetTokenData()}</h3> */}
                        <h3>Никнейм</h3>
                        <button>Выйти</button>
                    </div>

                    <div className='notes-container'>
                        {notes.map((note) => (
                            
                            <Link to={`/${note.id}`} state={{noteDetails: note, sharedKey}}><Notes note={note}></Notes></Link>
                    
                        ))}
                    </div>

                    <button className='add-button' onClick={openModal}>Добавить запись</button>
                </div>

                <div className="modal-overlay" style={{ display: isOpen ? 'flex' : 'none' }}>
                    <div className="modal">
                        <form id='add-el' className='form-add'>
                            <div className='part-form'>
                                <label htmlFor="title">Title</label>     
                                <input name='title'></input>
                            </div>
                            <div className='part-form'>
                                <label htmlFor="description">Description</label>     
                                <textarea rows={4} name='description' style={{resize: 'none'}}></textarea>
                            </div>
                        </form>

                        <button className='add-button' form='add-el'>Добавить</button>
                        <button className='close-button' onClick={closeModal}>
                            <img src="static\close-square-svgrepo-com.svg" alt="del" />
                        </button>
                    </div>
                </div>

            </div>
        </div>
    );
}

function Notes( {note} ) {
    return(
        <div className='note'>
            <div className='title-note'>
                <span>{note.title}</span>
                <span className='date-note'>{note.createdAt}</span>
            </div>
            <button>
                <img src="static\trash-svgrepo-com.svg" alt="del" />
            </button>

        </div>
    );
}
