/* global BigInt */

import bigInt from 'big-integer';
import CryptoJS from 'crypto-js';


// Генерация числа b
export function generateRandomNumber(minNum = 10, highNum = bigInt(10).pow(50)) {
    const randomNumber = bigInt.randBetween(minNum, highNum);

    console.log("Сгенерированное число:", randomNumber.toString());

    return randomNumber;
}

// Расчет числа B
export function calculateNumberB(g, b, p) {
    const gBig = BigInt(g); // Преобразуем g в big-integer
    const bBig = BigInt(b); // Преобразуем b в big-integer
    const pBig = BigInt(p); // Преобразуем p в big-integer

    const result = gBig.mod(pBig).pow(bBig.mod(pBig)).mod(pBig);

    return result;
}

// Расчет общего числа
export function calculateNumberS(A, b, p) {
    const ABig= BigInt(A)
    const bBig= bigInt(b)
    const pBig= bigInt(p)

    const result = ABig.mod(pBig).pow(bBig.mod(pBig)).mod(pBig);
}

export async function deriveKey(sharedKey) {
    // Преобразуем sharedKey (число) в ArrayBuffer
    const sharedKeyBuffer = new Uint8Array(
      BigInt(sharedKey).toString(16).match(/.{1,2}/g).map((byte) => parseInt(byte, 16))
    );
  
    // Константы HKDF
    const salt = new Uint8Array(); // Используем null для соли
    const info = new TextEncoder().encode('diffie-hellman encryption'); // Информация для HKDF
  
    // Импортируем sharedKey как ключ
    const baseKey = await crypto.subtle.importKey(
      'raw',
      sharedKeyBuffer,
      { name: 'HKDF' },
      false,
      ['deriveBits', 'deriveKey']
    );
  
    // Генерируем производный ключ
    const derivedKey = await crypto.subtle.deriveKey(
      {
        name: 'HKDF',
        hash: 'SHA-256',
        salt: salt,
        info: info,
      },
      baseKey,
      { name: 'AES-CBC', length: 256 }, // Тип производного ключа
      true,
      ['encrypt', 'decrypt']
    );
  
    // Экспортируем ключ в формате ArrayBuffer
    const rawKey = await crypto.subtle.exportKey('raw', derivedKey);
  
    // Возвращаем ключ в виде массива байтов
    return uint8ArrayToWordArray(new Uint8Array(rawKey));
  }
  

  function uint8ArrayToWordArray(u8Array) {
    const words = [];
    for (let i = 0; i < u8Array.length; i += 4) {
        words.push(
            (u8Array[i] << 24) |
            (u8Array[i + 1] << 16) |
            (u8Array[i + 2] << 8) |
            u8Array[i + 3]
        );
    }
    return CryptoJS.lib.WordArray.create(words, u8Array.length);
}
