import CryptoJS from 'crypto-js';

export function encryptMessage(sharedKey, message) {
     // Генерация IV
     const iv = CryptoJS.lib.WordArray.random(16).toString(CryptoJS.enc.Base64);
     const ivWordArray = CryptoJS.enc.Base64.parse(iv);
 

     
     // Проверка sharedKey
     if (!sharedKey || sharedKey.length !== 16) {
         console.error("Ошибка: sharedKey должен быть строкой длиной 16 символов.");
         throw new Error("Некорректный sharedKey");
     }
 
     // Шифрование
     const encryptedMessage = CryptoJS.AES.encrypt(
         JSON.stringify(message), // Преобразование в JSON
         CryptoJS.enc.Utf8.parse(sharedKey),
         {
             iv: ivWordArray,
             mode: CryptoJS.mode.CBC,
             padding: CryptoJS.pad.Pkcs7,
         }
     ).toString();
 
     console.log("Зашифрованное сообщение:", encryptedMessage);
     console.log("IV:", iv);
 
     return { iv: iv, encryptedMessage: encryptedMessage };
 }


// Функция для расшифровки сообщения
export function decryptMessage(ivBase64, encryptedMessageBase64, sharedKey) {
    const iv = CryptoJS.enc.Base64.parse(ivBase64);
    const encryptedMessage = CryptoJS.enc.Base64.parse(encryptedMessageBase64);
  
    const decrypted = CryptoJS.AES.decrypt(
      { ciphertext: encryptedMessage },
      sharedKey, // Преобразуем sharedKey
      {
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7,
      }
    );
  
    const plaintext = decrypted.toString(CryptoJS.enc.Utf8);
    return JSON.parse(plaintext); // Предполагаем, что сообщение в формате JSON
  }
