import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { decryptMessage, encryptMessage } from "./cryptoTools";
import {
  generateRandomNumber,
  calculateNumberB,
  calculateNumberS,
  deriveKey,
} from "./numberTools";
import { getKeys, sendKeyB, getMessage } from "./requests/requestExchangeKey";

function App() {
  const [b, setb] = useState(generateRandomNumber());
  console.log(`Сгенерированное число b: ${b}`)
  const [B, setB] = useState(null);
  const [S, setS] = useState(null);
  const [derivedKey, setDerivedKey] = useState(undefined);
  const [decryptedMessage, setDecryptedMessage] = useState(null);

  // Получение ключей от сервера при монтировании компонента
  useEffect(() => {
    async function exchangeKeys() {
      try {

        console.log(`Запрос getKeys...`);

        const keys = await getKeys(); // Получение g, A, p от сервера
        console.log(`Запрос getKeys закончен`);

        console.log("Полученные ключи:", keys);
        console.log(`Число g: ${keys.key_g}`);
        console.log(`Число p: ${keys.key_p}`);
        console.log(`Число A: ${keys.key_A}`);


        console.log(`Началась функция calculateNumberB`)
        const B_local = calculateNumberB(keys.key_g, b, keys.key_p); // Вычисление B
        console.log(`Закончилась функция calculateNumberB`)
        setB(B_local);
        console.log(`Полученн функция calculateNumberB`)
        sendKeyB(B_local); // Отправка B серверу

        const S_local = calculateNumberS(keys.key_A, b, keys.key_p); // Вычисление S
        setS(S_local);

        const derivedKey = await deriveKey(S_local); // Генерация ключа
        setDerivedKey(derivedKey);

        const dataMessage = await getMessage(); // Получение зашифрованного сообщения
        const decrypted = decryptMessage(
          dataMessage.iv,
          dataMessage.encrypted_message,
          derivedKey
        );
        setDecryptedMessage(decrypted);
      } catch (error) {
        console.error("Ошибка обмена ключами:", error);
      }
    }

    exchangeKeys();
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            <div>
              <h1>React + Diffie-Hellman</h1>
              <p>b: {b.toString()}</p>
              <p>B: {B ? B.toString() : "Загрузка..."}</p>
              <p>S: {S ? S.toString() : "Загрузка..."}</p>
              <p>Ключ: {derivedKey ? derivedKey.toString() : "Загрузка..."}</p>
              <p>Сообщение: {decryptedMessage || "Загрузка..."}</p>
            </div>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
