import React, { useState } from 'react';
import { generateDhParameters, computeSharedKey } from './diffieUtils';

const DiffieHellmanExchange = () => {
  const [sharedKey, setSharedKey] = useState(null);
  const [error, setError] = useState(null);

  // Обработчик обмена ключами
  const handleKeyExchange = async () => {
    try {
      // Генерация параметров Diffie-Hellman и публичного ключа
      const { dh, publicKey } = generateDhParameters();

      // Отправка публичного ключа на сервер
      const response = await fetch('http://127.0.0.1:5000/diffie/exchange', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          public_key: publicKey.toString('base64'),
        }),
      });

      if (response.ok) {
        const data = await response.json();

        // Получение публичного ключа сервера
        const serverPublicKey = Buffer.from(data.server_public_key, 'base64');

        // Вычисление общего ключа
        const sharedKey = computeSharedKey(dh, serverPublicKey);

        // Преобразование общего ключа в строку для отображения (например, в hex-формате)
        setSharedKey(sharedKey.toString('hex'));
      } else {
        setError('Ошибка обмена ключами');
      }
    } catch (err) {
      setError(`Ошибка: ${err.message}`);
    }
  };

  return (
    <div>
      <h1>Обмен ключами Diffie-Hellman</h1>
      <button onClick={handleKeyExchange}>Начать обмен</button>
      {sharedKey && (
        <div>
          <h2>Общий ключ:</h2>
          <p>{sharedKey}</p>
        </div>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default DiffieHellmanExchange;
